    /**
     * @test
     *
     * @group apiGetAll
     */
    public function unauthenticated_users_are_not_allowed_to_get_all_their_orders(): void
    {
        $res = $this->sendRequest()->json();

        $this->assertEquals('Unauthenticated.', $res['developer_message']);
        $this->assertEquals('Sorry there was a system error, the administrator has been informed.', $res['user_message']);
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function it_returns_the_correct_format(): void
    {
        $this->signIn()
            ->sendRequest()
            ->assertJsonStructure([
                'message',
                'data',
                'next',
                'records',
                'total_records',
            ]);
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function it_returns_all_orders_for_the_user(): void
    {
        $res = $this->signIn($this->user)->sendRequest();

        $res->assertJsonStructure([
            'data' => [
                [
                    "id",
                    "user_id",
                    "delivery_type_id",
                    "voucher_code_id",
                    "address_id",
                    "original_price",
                    "subtotal",
                    "total_price",
                    "total_discount",
                    "delivery_cost",
                    "status",
                    "currency_code",
                    "deleted_at"
                ],
            ],
        ]);

        $this->assertCount(2, $res->json('data'));
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function soft_deleted_addresses_are_not_returned(): void
    {
        $this->orders[0]->deleted_at = Carbon::now();
        $this->orders[1]->deleted_at = Carbon::now();
        $this->orders[0]->save();
        $this->orders[1]->save();
        $res = $this->signIn($this->user)->sendRequest();

        $this->assertEmpty($res->json('data'));
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function it_returns_the_count(): void
    {
        $this->assertEquals(2, $this->signIn($this->user)->sendRequest()->json('total_records'));
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function it_returns_all_required_fields(): void
    {
        $this->signIn($this->user)
            ->sendRequest()
            ->assertJsonStructure([
                'data' => [
                    app()->make(OrderRepository::class)->getSelectableColumns(false),
                ],
            ]);
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function orders_can_be_filtered_by_id(): void
    {
        $res = $this->signIn($this->user)->sendRequest(['filter[id]' => $this->orders[1]->id]);

        $this->assertCount(1, $res->json('data'));
        $this->assertEquals($this->orders[1]->id, $res->json('data.0.id'));
    }

    /**
     * @test
     *
     * @group apiGetAll
     */
    public function filters_can_accept_multiple_parameters(): void
    {
        $res = $this->signIn($this->user)->sendRequest(['filter[id]' => implode(',', [$this->orders[0]->id, $this->orders[1]->id])]);

        $this->assertCount(2, $res->json('data'));
        $this->assertEquals($this->orders[0]->id, $res->json('data.0.id'));
        $this->assertEquals($this->orders[1]->id, $res->json('data.1.id'));
    }

    protected function sendRequest($data = []): TestResponse
    {
        return $this->getJson(route('api.orders.getAll', $data));
    }
}
